import requests
from bs4 import BeautifulSoup
import json
import time

def scrape_station_info(url):
    response = requests.get(url)
    response.raise_for_status()
    soup = BeautifulSoup(response.content, 'html.parser')
    
    station_name_elem = soup.find('h1', class_='stName')
    if station_name_elem:
        station_name_parts = station_name_elem.find_all('span', class_=['stName1'])
        station_name = ''.join(part.text for part in station_name_parts).strip()
    else:
        station_name = "不明な駅名"
    
    facilities = {
        'station': station_name,
        '車いす対応エレベータ': -1,
        'エレベータ': -1,
        'エスカレータ': -1,
        '車いす対応トイレ': -1,
        'トイレ': -1,
        'オストメイト対応トイレ': -1,
        'ベビーシート対応トイレ': -1,
        '車いす対応スロープ': -1,
        '車いす対応チェアメイト': -1,
        '誘導ブロック': -1,
        '点字運賃表': -1,
        '点字券売機': -1,
        '駅員無配置': -1
    }
    
    facility_items = soup.find_all('li', class_='bfItem')
    # print(facility_items)
    for item in facility_items:
        facility_name = item.find('span', class_='bfTxt').text.strip()
        status_img = item.find('img', alt=['利用可能', '利用不可'])
        
        # if status_img and 'ico_available_01.svg' in status_img.get('src', ''):
        #     status = 1
        # else:
        #     status = -1
               
        status = 1 if status_img and '利用可能' in status_img.get('alt', '') else -1
            
        for key in facilities.keys():
            if key in facility_name:
                facilities[key] = status
                break
    print(facilities)
    return facilities

